CREATE TABLE Flight (flight_number varchar(5) NOT NULL, name varchar(255), departure_city varchar(255), Destination_City varchar(255), Departure_Date varchar(255), Departure_Time varchar(255), Arrival_Date varchar(255), Arrival_Time varchar(255), Business_Class_Seats int, Economy_Class_Seats int, Business_Class_Price int, Economy_Class_Price int, PRIMARY KEY(Flight_Number))